jQuery.golf.components={"TableExample":{"res":{"asc.gif":"components/TableExample/TableExample.res/asc.gif","desc.gif":"components/TableExample/TableExample.res/desc.gif","bg.gif":"components/TableExample/TableExample.res/bg.gif"},"name":"TableExample","html":"<div>\n\n  <style type=\"text/golf\">\n    * {\n      font-family: sans-serif;\n      color: #444;\n    }\n\n    a {\n      text-decoration: none;\n      color: inherit;\n    }\n\n    div.container {\n      margin: 0 auto;\n      width: 800px;\n      padding: 20px;\n    }\n\n    table.jquery-tablesort > thead > tr > th > span,\n    table.jquery-tablesort > thead > tr > th > a > span {\n      text-align: left;\n      cursor: pointer;\n      padding-right: 20px;\n      background-image: url('components/TableExample/TableExample.res/bg.gif');\n      background-position: center right;\n      background-repeat: no-repeat;\n    }\n\n    table.jquery-tablesort > thead > tr > th > span.sort-asc,\n    table.jquery-tablesort > thead > tr > th > a > span.sort-asc {\n      background-image: url('components/TableExample/TableExample.res/asc.gif');\n    }\n\n    table.jquery-tablesort > thead > tr > th > span.sort-desc,\n    table.jquery-tablesort > thead > tr > th > a > span.sort-desc {\n      background-image: url('components/TableExample/TableExample.res/desc.gif');\n    }\n\n    table.properties {\n      border: 1px solid #444;\n      margin: 0px auto;\n    }\n\n    table.properties > thead > tr > th {\n      border-bottom: 1px solid #444;\n      padding: 5px 10px;\n      margin: 0px;\n    }\n\n    table.properties > tfoot > tr > td {\n      border-top: 1px solid #444;\n      padding: 5px 10px;\n      margin: 0px;\n    }\n\n    table.properties > tbody > tr > td {\n      padding: 5px 10px;\n      border-bottom: 1px solid #ccc;\n      border-left: 1px dashed #ddd;\n    }\n\n    table.properties > tbody > tr > td:first-child {\n      border-left: none;\n    }\n\n    table.properties > tbody > tr:last > td {\n      border-bottom: none;\n    }\n\n    table.properties > tbody > tr:nth-child(odd) {\n      background: #eee;\n    }\n  <\/style>\n\n  <script type=\"text/golf\">\n    \n    function(data) {\n\n      $(\".container\").append(\n        $(data)\n          .tableize()\n          .addClass(\"properties\")\n          .thead([\"one\",\"two\",\"three\"])\n          .tfoot([\"one\",\"two\",\"three\"])\n          .talign([\"left\",\"right\",\"left\"])\n          .twidth([100,200,400])\n          .tcaption(\"Hello, Tables\")\n      );\n\n      $(\"table.properties\").sortable();\n\n    };\n\n  <\/script>\n\n  <div class=\"container\">\n  <\/div>\n\n<\/div>\n"}};jQuery.golf.res={"plugins":{"splat_routes.js":"plugins/splat_routes.js"},"qunit.js":"qunit.js","README.markdown":"README.markdown"};jQuery.golf.plugins={"splat_routes":{"name":"splat_routes","js":"(function() {\n\n  $.map($.golf.controller, function(v,k) {\n    if (v.splat_route)\n      v.route = \"^\" + $.map(v.splat_route.split(\"\"), function(w,i) {\n        return (w == \"*\" ? \"([^/]*)\" : (w.match(/[a-zA-Z1-9]/) ? w : \"\\\\\"+w));\n      }).join(\"\") + \"$\";\n  });\n\n})();\n"}};jQuery.golf.scripts={"jquery.tableize":{"name":"jquery.tableize","js":"(function($) {\n  \n  $.fn.tableize = function() {\n    return $(\"<table cellspacing='0' cellpadding='0'><\/table>\")\n      .append(\"<caption><\/caption>\")\n      .append(\"<colgroup><\/colgroup>\")\n      .append(\"<thead><\/thead>\")\n      .append(\"<tfoot><\/tfoot>\")\n      .append($(\"<tbody/>\").append($(this).map(function(k,v) {\n        return $(\"<tr/>\").append($(v).map(function(kk,vv) {\n          return $(\"<td/>\")[vv.jquery||vv.nodeName?\"append\":\"text\"](vv)[0];\n        }))[0]\n      }).get()));\n  };\n\n  $.each([\"thead\",\"tfoot\"], function(k,v) {\n    $.fn[v] = function(h) {\n      return this.each(function(kk,vv) {\n        $(vv).find(v).append($(\"<tr/>\").append($(h).map(function(kkk,vvv) {\n          var elem = v == \"thead\" ? \"<th/>\" : \"<td/>\";\n          return $(elem).append($(\"<span/>\").text(vvv))[0];\n        })))\n      });\n    };\n  });\n  \n  $.fn.twidth = function(h) {\n    return this.each(function(k,v) {\n      $(v).find(\"colgroup\").empty().append($(h).map(function(kk,vv) {\n        return $(\"<col/>\").attr(\"width\", vv)[0];\n      }))\n    });\n  };\n\n  $.fn.talign = function(h) {\n    return this.each(function(k,v) {\n      $(v).find(\"tr\").not($(\"thead tr\")).each(function(kk,vv) {\n        $(vv).find(\"td\").each(function(kkk,vvv) {\n          $(vvv).attr(\"align\", h[kkk]);\n        });\n      });\n    });\n  };\n\n  $.fn.tcaption = function(cap) {\n    this.find(\"caption\").text(cap);\n    return this;\n  };\n\n})(jQuery);\n"},"jquery.tablesort":{"name":"jquery.tablesort","js":"/**\n * jquery.tablesort.js: Tiny table sorting script for jQuery.\n *\n * Fork me on github:\n *   http://github.com/micha/jquery-tablesort/\n *\n * Author:\n *   Micha Niskin <micha@thinkminimo.com>\n *   Copyright 2009, no rights reserved.\n */\n(function($) {\n  $.fn.sortable = function() {\n    this.each(function(i_table, v_table) {\n      var tbl = $(this).addClass(\"jquery-tablesort\");\n      if (tbl.get()[0].tagName.toUpperCase() == \"TABLE\") {\n        $(\"th > *:first-child\", tbl).each(function(i_col, v_col) {\n          var th = $(this);\n          th.click(function() { \n            var not  = tbl.find(\"td table *\");\n            tbl.find(\"tbody\").not(not).each(function(i_tbody, v_tbody) {\n              var rows = $(v_tbody).find(\"tr\").not(not);\n              var bak  = [], sort_as = null;\n              rows.each(function(i_row, v_row) {\n                var td = bak[i_row] = \n                  $(this).find(\"td\").not(not).eq(i_col).text()+\"\";\n                var type =  \n                  (!isNaN(Date.parse(td)) ? \"date\" \n                    : (!isNaN(new Number(td)) ? \"number\" \n                      : (!isNaN(new Number(td.replace(/^\\$/,\"\"))) ? \"currency\" \n                        : \"string\")));\n                sort_as = (!!sort_as && sort_as != type ? \"string\" : type);\n              });\n              rows = rows.sort(function(a, b) {\n                var va = $(a).find(\"td\").not(not).eq(i_col).text();\n                var vb = $(b).find(\"td\").not(not).eq(i_col).text();\n                if (sort_as == \"date\") {\n                  va = Date.parse(va);\n                  vb = Date.parse(vb);\n                  return (va < vb ? -1 : (va == vb ? 0 : 1));\n                } else if (sort_as == \"currency\") {\n                  return (va.replace(/^\\$/, \"\") - vb.replace(/^\\$/, \"\"));\n                } else if (sort_as == \"number\") {\n                  return (va - vb);\n                } else if (sort_as == \"string\") {\n                  va = va.toString();\n                  vb = vb.toString();\n                  return (va < vb ? -1 : (va == vb ? 0 : 1));\n                } else {\n                  return 0;\n                }\n              });\n              $(\".sort-asc\", tbl).not(not).removeClass(\"sort-asc\");\n              $(\".sort-desc\", tbl).not(not).removeClass(\"sort-desc\");\n              if ((function() {\n                for (var i=0; i<rows.size(); i++)\n                  if (rows.eq(i).find(\"td\").not(not).eq(i_col).text() != bak[i])\n                    return false;\n                return true;\n              })()) {\n                rows = $(rows.get().reverse());\n                th.removeClass(\"sort-asc\").addClass(\"sort-desc\");\n              } else {\n                th.removeClass(\"sort-desc\").addClass(\"sort-asc\");\n              }\n              $(v_tbody).append(rows);\n            });\n            tbl.trigger('sort');\n          });\n        });\n        tbl.trigger('sort');\n      }\n    });\n    return this;\n  };\n})(jQuery);\n"}};jQuery.golf.styles={"main":{"name":"main","css":"body { margin:0; padding:0; } "}};jQuery.golf.setupComponents();